import React, { useState } from "react";

function DonationForm(){
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [genre, setGenre] = useState('');
    const [image, setImage] = useState('');

    function handleSubmit(e){
        e.preventDefault();
        fetch('http://localhost:3000/books', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                title: e.target.title.value,
                author: e.target.author.value,
                genre: e.target.genre.value,
                image: e.target.image.value,
                inStock: 1
            })
        })
        .then((res)=>res.json())
        .then(data => {
            console.log(data)
            console.log('Thank you for your donation!')
        })
    }
    return (
        <div className='Donation-form'>
            <form onSubmit={(e)=>handleSubmit(e)}>
                <h2>Donate a new Album: </h2>
                <label className='column1'>Title: </label>
                <input onChange={(e)=>(setTitle(e.target.value))} className='column2'type="text" name="title" /><br/>
                <label className='column1'>Author: </label>
                <input onChange={(e)=>(setAuthor(e.target.value))} className='column2' type="text" name="author" /><br/>
                <label className='column1'>Genre: </label>
                <input onChange={(e)=>(setGenre(e.target.value))} className='column2'type="text" name="genre" /><br/>
                <label className='column1'>Image URL: </label>
                <input onChange={(e)=>(setImage(e.target.value))}className='column2'type="text" name="image" /><br/>
                <button type="submit">Donate</button>
            </form>
        </div>
    )
}

export default DonationForm;
