import React, {useState} from 'react';

function Album({album, setMessage}) {
    let loading = false

    function handleDonate(id) {
        if(loading) return;
        loading = true
        fetch(`http://localhost:3000/albums/${id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                inStock: album.inStock + 1
            })
        })
        .then(res => res.json())
        .then(data => {
            loading = false
            setMessage('Thank you for your donation!')
        })
        .catch(err => console.log(err))
    }

    return (
        <div className='Album-card'>
            <img className='Album-image' src={album.image} alt={album.title}/>
            <p><span className='column1'>Title:</span> <span className='column2'>{album.title}</span></p>
            <p><span className='column1'>Artist:</span> <span className='column2'>{album.artist}</span></p>
            <p><span className='column1'>Length:</span> <span className='column2'>{album.length}</span></p>
            <br/>
            <span>
                {loading ? <p>...</p> : <button onClick={()=>handleDonate(album.id)}>Donate</button>}
                <p>{album.inStock} in stock.</p>
            </span>

        </div>
    )
}

export default Album;
